mapper.select:
select 

from bas_consumables_storage
where instrumentId = #{instrumentId,jdbcType=VARCHAR}

and batch = #{batch,jdbcType=VARCHAR}

     select 
     s.id, s.instrumentId, s.instrumentName, s.firm, s.spec, s.batch, sum(s.number) as number, 
     s.minPackageUnit, s.productionTime,s.effectiveTime, s.pinYin, s.beid
     from bas_consumables_storage s where 1=1






								and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	group by instrumentName,firm,spec
  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

select s.id, s.instrumentId, s.instrumentName, s.firm, s.spec, s.batch, s.minPackageUnit, s.productionTime,s.effectiveTime,s.pinYin, (SELECT statisticsNum FROM bas_consumables_storage_his his WHERE DATE_FORMAT( date_add(his.statisticsTime, interval 1 MONTH) ,'%Y-%m') = #{queryMonth} and his.beid = s.beid and his.storageId = s.id) as lastMonthAllNum, (select sum(i.number) from bas_consumables_in_record i where i.`status` = 1 and DATE_FORMAT(i.operateTime,'%Y-%m') = #{queryMonth} and i.beid = s.beid and i.instrumentName=s.instrumentName and i.firm =s.firm and i.spec = s.spec and i.batch = s.batch) as monthAllInNum, (select sum(ou.outNumber) from bas_consumables_out_record ou where ou.`enable` = 1 and DATE_FORMAT(ou.outTime,'%Y-%m') = #{queryMonth} and ou.beid = s.beid and ou.instrumentName=s.instrumentName and ou.firm =s.firm and ou.spec = s.spec and ou.batch = s.batch ) as monthAllOutNum, (select sum(ou.retreatNumber) from bas_consumables_out_record ou where ou.`enable` = 1 and DATE_FORMAT(ou.outTime,'%Y-%m') = #{queryMonth} and ou.beid = s.beid and ou.instrumentName=s.instrumentName and ou.firm =s.firm and ou.spec = s.spec and ou.batch = s.batch ) as monthAllRetreatNum, ( (select IFNULL(sum(ou.loseNumber),0) from bas_consumables_out_record ou where ou.`enable` = 1 and DATE_FORMAT(ou.outTime,'%Y-%m') = #{queryMonth} and ou.beid = s.beid and ou.instrumentName=s.instrumentName and ou.firm =s.firm and ou.spec = s.spec and ou.batch = s.batch) + (select IFNULL(sum(los.loseNumber),0) from bas_consumables_lose_record los where los.loseType='3' and los.`enable` = 1 and DATE_FORMAT(los.loseTime,'%Y-%m') = #{queryMonth} and los.beid = s.beid and los.storageId = s.id) ) as monthAllLoseNum, (SELECT statisticsNum FROM bas_consumables_storage_his his WHERE DATE_FORMAT(his.statisticsTime,'%Y-%m') = #{queryMonth} and his.beid = s.beid and his.storageId = s.id) as monthAllNum from bas_consumables_storage s where 1=1
and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))
and beid = #{filter.value}
AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')
order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}
limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

SELECT count(id) FROM ( select
from bas_consumables_storage where 1 = 1
and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))
and beid = #{filter.value}
AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')
) x

     select 
     instrumentId, instrumentName, firm, spec, batch, minPackageUnit,outTime,receiveName,outType,enable,pinYin,beid,
     sum(outNumber) as allOutNumber,
     sum(retreatNumber) as allRetreatNumber,
     sum(loseNumber) as allLoseNumber,
     sum(actualNumber) as allActualNumber
     from bas_consumables_out_record  where `enable` = 1






								and  DATE_FORMAT(outTime,'%Y-%m-%d %H:%i') >= #{filter.value}




								and DATE_FORMAT(outTime,'%Y-%m-%d %H:%i') <= #{filter.value}




								and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	group by receiveName,instrumentName,firm,spec
  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

    SELECT count(id) FROM (
	 select 
      id, instrumentId, instrumentName, firm, spec, batch, minPackageUnit,outTime,receiveName,outType,enable,pinYin,beid
     from bas_consumables_out_record 
	 where `enable` = 1






								and DATE_FORMAT(outTime,'%Y-%m-%d') >= #{filter.value}




								and DATE_FORMAT(outTime,'%Y-%m-%d') <= #{filter.value}




								and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






	group by receiveName,instrumentName,firm,spec
	) x

     select 

     from bas_consumables_storage where number > 0






								and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))




								and beid = #{filter.value}




								AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')






  	order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

SELECT count(id) FROM ( select
from bas_consumables_storage where number > 0
and (instrumentName LIKE CONCAT(CONCAT('%',#{filter.value}),'%') or pinYin LIKE CONCAT('%',#{filter.value},'%'))
and beid = #{filter.value}
AND ${filter.field} LIKE CONCAT(CONCAT('%',#{filter.value}),'%')
) x
