mapper.select:
SELECT  *
FROM doc_event_billing d
WHERE 1 = 1
${filter}
order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

 	 limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

SELECT count(*) FROM doc_event_billing
WHERE 1 = 1
${filter}
order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

SELECT count(*) FROM doc_event_billing
WHERE regOptId = #{regOptId} and state = 'FINISH' and isCharged = 1 and source !='2'
	and costType = #{costType}

SELECT *
FROM doc_event_billing d left join bas_reg_opt b on d.regOptId = b.regOptId
WHERE d.ebId = #{ebId}

SELECT x.docId,x.medEventId objectId,m.medicineId CODE,m.name,m.briefName otherName, p.firm,p.firmId,p.spec,p.pharmaciesCode,p.pharmaciesName, m.packageDosageAmount, dosage AS dosageTotalAmount, dosageUnit, minPackageUnit, priceMinPackage,x.createuser createUser,'1' source,'1' chargedType,1 AS discount,1 isCharged,starttime AS createTime FROM ( SELECT a.* FROM evt_medicalevent a WHERE docId=#{baseQuery.docId}) x LEFT JOIN bas_medicine m ON m.medicineId = x.medicineId LEFT JOIN bas_price p ON x.priceId = p.priceId WHERE 1=1 AND p.beid = #{beid} AND m.beid = #{beid}
AND m.medicineId = #{baseQuery.id}
order by medEventId ASC
limit #{baseQuery.pageNo},#{baseQuery.pageSize}

SELECT x.docId,x.ineventId objectId,m.code CODE,m.name,m.briefName otherName, p.firm,p.spec,p.pharmaciesCode,p.pharmaciesName, m.packageDosageAmount, dosageAmount AS dosageTotalAmount, dosageUnit, minPackageUnit, priceMinPackage, x.createuser createUser,'1' source,'I' chargedType,1 AS discount,1 isCharged,starttime AS createTime FROM ( SELECT a.* FROM evt_inevent a WHERE docId=#{baseQuery.docId}) x left join bas_user b on x.createuser=b.userName LEFT JOIN bas_io_defination m ON m.ioDefId = x.ioDefId LEFT JOIN bas_price p ON x.priceId = p.priceId WHERE 1=1 AND b.beid = #{beid} AND m.beid = #{beid} AND p.beid = #{beid}
AND m.ioDefId = #{baseQuery.id}
order by x.ineventId ASC
limit #{baseQuery.pageNo},#{baseQuery.pageSize}

select b.`name`as regName ,tol.* FROM ( SELECT regOptId,code,NAME,firm,firmId,spec,doc_event_billing.state,ebId, doc_event_billing.packageDosageAmount, priceMinPackage, dosageTotalAmount, packageTotalAmount, #concat(CEIL(dosageTotalAmount / packageTotalAmount),minPackageUnit) totalAmountUnit, discount, shouldCost, #CEIL(packageTotalAmount / packageDosageAmount) * discount * priceMinPackage realCost, isCharged, chargedType,dosageUnit,minPackageUnit FROM doc_event_billing WHERE 1 = 1 ${filter} ) tol left join bas_reg_opt b on tol.regOptId=b.regOptId
order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}
limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

mapper.delete:
delete from doc_event_billing  where source = '1'

	AND regOptId = #{baseQuery.regOptId}


	AND userType = #{baseQuery.userType}

mapper.select:
select SUM(shouldCost) from (
select ROUND(sum(shouldCost),2) shouldCost from doc_event_billing where regOptId = #{regOptId}
and costType = #{costType}
UNION 
select ROUND(IFNULL(sum(a.chargeAmount*b.price*a.discount),0),2) shouldCost 
from doc_packages_item a,bas_charge_item b where a.regOptId = #{regOptId}
and a.chargeItemId = b.chargeItemId
and costType = #{costType}
) x
