mapper.select:
   		select * from bas_anaes_event
   		where 1 = 1
   		and beid = #{systemSearchFormBean.beid} ${filter} 
		order by ${systemSearchFormBean.sort} ${systemSearchFormBean.orderBy}

		 	 limit #{systemSearchFormBean.pageNo},#{systemSearchFormBean.pageSize}

		select eventValue codeValue, name codeName from bas_anaes_event 
		where enable = 1 and beid = #{beid}

     and eventValue = #{code}

select * from bas_anaes_event
where enable = 1 and beid = #{baseQuery.beid}

	AND pinyin LIKE CONCAT(CONCAT('%',#{baseQuery.pinyin}),'%')

order by eventValue asc;


select x2.* from (
	 select code, count(*) cnt From evt_anaesevent t,doc_anaes_record r,bas_reg_opt b 
		where t.docId = r.anaRecordId and r.regOptId = b.regOptId and b.beid = #{baseQuery.beid}
		and code not in (1,2,4,5,8,9,1001,1002)	

			AND t.docType = #{baseQuery.docType}

		GROUP BY t.code
	) x1 JOIN   
	(
		select * from bas_anaes_event
			where enable = 1 and beid = #{baseQuery.beid}

				AND pinyin LIKE CONCAT(CONCAT('%',#{baseQuery.pinyin}),'%')


	) x2 on x1.code = x2.eventValue ORDER BY cnt DESC LIMIT 8
