mapper.select:
select 

from bas_device_config
where roomId = #{roomId,jdbcType=VARCHAR} and deviceId = #{deviceId,jdbcType=VARCHAR} and beid = #{beid,jdbcType=VARCHAR}

SELECT a.deviceId, b.deviceModel AS deviceName, a.roomId, b.deviceType AS modelId, b.protocol, a.`status`, a.`beid` FROM bas_device_config a, bas_device_specification b WHERE a.deviceId = b.deviceId AND a.enable = '1'
AND a.roomId = #{roomId}
and a.beid = #{beid}
group by deviceId

mapper.delete:
    	delete from bas_device_config
    	where beid = #{beid}

			AND deviceId = #{deviceId}


			AND roomId = #{roomId}

mapper.update:
UPDATE bas_device_config SET 
	status = #{status}
WHERE deviceId = #{deviceId} 
and beid = #{beid}
and roomId = #{roomId}

mapper.select:
select a.*,b.deviceType,b.deviceModel from bas_device_config a,bas_device_specification b
WHERE a.deviceId = b.deviceId and 
	a.enable = '1' and a.beid = #{beid,jdbcType=VARCHAR} and a.roomId = #{roomId,jdbcType=VARCHAR}
